//     )
// }

class Comments extends React.Component{

    constructor(){
        super()
        this.state = {
            inputContent: ""
        }
    }

    render(){
      return(
        <Comment.Group style={{marginLeft:"350px"}}>
        <Form reply>
            <Form.TextArea value={this.state.inputContent} placeholder="댓글을 입력하세요" onChange={(event)=>this.setState({inputContent: event.target.value})}/>
            <Button
            content='후기 작성'
            labelPosition='left'
            icon='edit'
            secondary
            onClick={()=> alert(this.state.inputContent)}
            />
        </Form>

        </Comment.Group>
      )
    }
  }

  export default Comments
