import React from 'react';
import {Comment, Form, Button, Header} from 'semantic-ui-react';

import sw2 from "./sw2.jpeg"
import sw3 from "./sw3.jpeg"
import sw4 from "./sw4.jpeg"
import sw5 from "./sw5.jpeg"

function SingleComment (detail) {
    return(
        <Comment>
        <Comment.Content>
        <Comment.Avatar as='a' src={sw2}/>
        <Comment.Author as='a'>세원</Comment.Author>
        <Comment.Metadata>
          <div>2 days ago</div>
        </Comment.Metadata>
        <Comment.Text>{detail.content}</Comment.Text>
        </Comment.Content>
        </Comment>
    )
}

class Comments extends React.Component{

    constructor(){
        super()
        this.state = {
            inputContent: "",
            commentsList : []
        }
    }

    render(){
        console.log(this.state.commentsList)
      return(
        <Comment.Group style={{marginLeft:"350px"}}>
            <Header as='h3' dividing>댓글</Header>

        {this.state.commentsList.map(comments => <SingleComment content ={comments}/>)}

        <Form reply>
            <Form.TextArea 
            value={this.state.inputContent} 
            placeholder="댓글을 입력하세요" 
            onChange={(e) => this.setState({inputContent: e.target.value})}
            />
            <Button
            content='후기 작성'
            labelPosition='left'
            icon='edit'
            secondary
            onClick = {() => this.setState((prevState) => { 
                return {
                    commentList: [...prevState.commentsList, this.state.inputContent]
                };
            })}
            />
        </Form>
        </Comment.Group>
      )
    }
  }

  export default Comments
