import React from 'react';
import {Comment, Form, Button, Header} from 'semantic-ui-react';

import sw2 from "./sw2.jpeg"
import sw3 from "./sw3.jpeg"
import sw4 from "./sw4.jpeg"
import sw5 from "./sw5.jpeg"

function SingleComment (detail) {
    return(
        <Comment>
        <Comment.Avatar as='a' src={sw2}/>
        <Comment.Author as='a'>Steve Jobes</Comment.Author>
        <Comment.Metadata>
          <div>2 days ago</div>
        </Comment.Metadata>
        <Comment.Text>덕분에 진로 고민이 해결되었어요!</Comment.Text>
        </Comment>
    )
}

class Comments extends React.Component{

    constructor(){
        super()
        this.state = {
            inputContent: ""
        }
    }

    render(){
      return(
        <Comment.Group style={{marginLeft:"350px"}}>
        <Comment>
        <Form reply>
            <Form.TextArea value={this.state.inputContent} placeholder="댓글을 입력하세요" onChange={(event)=>this.setState({inputContent: event.target.value})}/>
            <Button
            content='후기 작성'
            labelPosition='left'
    icon='edit'
    secondary
    onClick={()=> alert(this.state.inputContent)}
  />
</Form>
        </Comment>
      </Comment.Group>
      )
    }
  }

  export default Comments
